package package1;
// write a program using conditional operators 

public class Conditional_Operators 
{
	public static void main(String[] args)
	{
		// TODO Auto-generated method stub
		int a=10,b=30;
		
		if(a<b)
			System.out.println("a is less than b");
		if(a>b)
			System.out.println("a is greaterthan b");
		if(a<=b)
			System.out.println("a is less than equal to b");
		if(a>=b)
			System.out.println("a is greater than equal to b");
		if(a!=b)
			System.out.println("a is not equal to b");
		if(a==b)
			System.out.println("a is equal to b");
	}

}
